class Point:

    def __init__(self,x,y):
        self.x = x
        self.y = y

    def dist(self,p):
        return math.pow(math.pow(self.x-p.x,2)+math.pow(self.y-p.y,2),0.5)
    def dist2(self,p):
        return math.pow(self.x-p.x,2)+math.pow(self.y-p.y,2)
    def closest(self,a,b):
        da = b.y - a.y
        db = a.x - b.x
        c1 = da*a.x + db*a.y
        c2 = -db*self.x + da*self.y
        det = da*da + db*db
        cx = 0
        cy = 0

        if (det != 0):
            cx = (da*c1 - db*c2)/det
            cy = (da*c2 + db*c1)/det
        else:
            cx = self.x
            cy = self.y

        p = Point(cx,cy)
        return p

cache = []

class Unit(Point):

    def __init__(self,x,y,id,type,r,vx,vy):
        Point.__init__(self,x,y)
        self.id = id
        self.type = type
        self.r = r
        self.vx = vx
        self.vy = vy
        self.cache = [0,0,0,0]


    #virtual void bounce(Unit* u) {};

    def collision_time(self,u):
        if (self.vx == u.vx and self.vy == u.vy):
            return -1

        if u.type == CP:
            sr2 = 357604
        else:
            sr2 = 640000

        dx = self.x - u.x
        dy = self.y - u.y
        dvx = self.vx - u.vx
        dvy = self.vy - u.vy
        a = dvx**2 + dvy**2

        if (a < E):
            return -1

        b = -2.0*(dx*dvx + dy*dvy)
        delta = b**2 - 4.0*a*(dx**2 + dy**2 - sr2)

        if (delta < 0.0):
            return -1


        t = (b - math.pow(delta,0.5))*(1.0/(2.0*a))

        if (t <= 0.0 or t > 1):
            return -1

        return t

    def save(self):
        self.cache[0] = self.x
        self.cache[1] = self.y
        self.cache[2] = self.vx
        self.cache[3] = self.vy


    def load(self):
        self.x = self.cache[0]
        self.y = self.cache[1]
        self.vx = self.cache[2]
        self.vy = self.cache[3]




