class Pod(Unit):

    def __init__(self,id,x,y):
        Unit.__init__(self,x,y,id,POD,400,0,0)
        self.angle = -1
        self.next_angle = -1
        self.has_boost = True
        self.ncpid = 1
        self.checked = 0
        self.timeout = 100
        self.shield = 0
        self.partner = 0
        self.pod_cache = [0,0,0,0,0,0]
        self.is_p2 = False
        self.cp_ct = 1
        self.laps = 3


    def boost(self,thrust):
        if (self.shield > 0):
            return
        else:
            ra = self.angle * math.pi/180
            self.vx += math.cos(ra)*thrust
            self.vy = math.sin(ra)*thrust

    def apply(self,thrust,angle):
        angle = max(-18,min(18,angle))
        self.angle += angle

        if (self.angle >= 360):
            self.angle = self.angle - 360
        elif (self.angle <= 0):
            self.angle += 360

        if (thrust == -1):
            self.shield = 4
        else:
            self.boost(thrust)


    def get_angle(self,p):
        d = (self.dist(p)+0.01)
        dx = (p.x - self.x)/d
        dy = (p.y - self.y)/d

        a = math.acos(dx)*180/math.pi
        if (dy < 0):
            a = 360 - a
        return a


    def diff_angle(self,p):
        a = self.get_angle(p)
        if (self.angle <= a):
            right = a - self.angle
        else:
            right = 360 + a - self.angle

        if (self.angle >= a):
            left = self.angle - a
        else:
            left = self.angle + 360 - a

        if (right < left):
            return right
        else:
            return -left


    def rotate(self,p):
        a = self.diff_angle(p)
        a = max(-18,min(18,a))

        self.angle += a

        if (self.angle >= 360):
            self.angle = self.angle - 360
        elif (self.angle < 0):
            self.angle += 360


    def move(self,t):
        self.x += t*self.vx
        self.y += t*self.vy

    def end(self):
        self.x = math.trunc(self.x)
        self.y = math.trunc(self.y)
        self.vx = math.trunc(self.vx*0.85)
        self.vy = math.trunc(self.vy*0.85)
        if (self.checked >= self.cp_ct * self.laps):
            self.ncpid = 0
            self.checked = self.cp_ct * self.laps
        self.timeout -= 1
        if (self.shield > 0):
            self.shield -= 1

    def apply_bot(self,game):
        cp = game.cps[self.ncpid]

        t = Point(cp.x-3*self.vx,cp.y-3*self.vy)


        raw_angle = self.diff_angle(t)

        thrust = 200
        angle = max(-18,min(18,raw_angle))
        self.apply(thrust,angle)

    def bounce_w_pod(self,u):
        if (self.shield == 4):
            m1 = 10
        else:
            m1 = 1

        if (u.shield == 4):
            m2 = 10
        else:
            m2 = 1

        mcoeff = (m1 + m2)/(m1*m2)
        nx = self.x - u.x
        ny = self.y - u.y
        dst2 = nx**2 + ny**2
        dvx = self.vx - u.vx
        dvy = self.vy - u.vy
        prod = (nx*dvx + ny*dvy)/(dst2 * mcoeff)
        fx = nx*prod
        fy = ny*prod
        m1_inv = 1.0/m1
        m2_inv = 1.0/m2

        self.vx -= fx*m1_inv
        self.vy -= fy*m1_inv
        u.vx += fx*m2_inv
        u.vy += fy*m2_inv

        impulse = math.pow(fx**2+fy**2,0.5)
        if (impulse < 120 and impulse > 0):
            df = 120.0/impulse
            fx *= df
            fy *= df

        self.vx -= fx*m1_inv
        self.vy -= fy*m1_inv
        u.vx += fx*m2_inv
        u.vy += fy*m2_inv


    def bounce(self,u):
        if (u.type == CP):
            self.checked += 1
            self.timeout = 100
            #self.partner.timeout = 100
            self.ncpid = (self.ncpid + 1)%self.cp_ct
            return
        else:
            self.bounce_w_pod(u)



    def update(self,x,y,vx,vy,angle,ncpid):
        if (self.shield > 0):
            self.shield -= 1
        if (ncpid != self.ncpid):
            self.timeout = 100
            #self.partner.timeout = 100
            self.checked += 1
        else:
            self.timeout -= 1

        self.x = x
        self.y = y
        self.vx = vx
        self.vy = vy
        self.ncpid = ncpid

        if (self.is_p2 == True and self.id > 1):
            a = copy.deepcopy(self.next_angle)
            self.next_angle = angle
            self.angle = a
        self.angle = angle

        Unit.save(self)


    def update_bis(self,shield,has_boost):
        self.shield = shield
        self.has_boost = has_boost

    def save(self):
        Unit.save(self)
        self.pod_cache[0] = self.ncpid
        self.pod_cache[1] = self.checked
        self.pod_cache[2] = self.timeout
        self.pod_cache[3] = self.shield
        self.pod_cache[4] = self.angle
        self.pod_cache[5] = self.has_boost


    def load(self):
        Unit.load(self)
        self.ncpid   = self.pod_cache[0]
        self.checked = self.pod_cache[1]
        self.timeout = self.pod_cache[2]
        self.shield  = self.pod_cache[3]
        self.angle   = self.pod_cache[4]
        self.has_boost = self.pod_cache[5]



